import React, { useState, useEffect } from "react";
import { useRoutes, usePath, A } from "hookrouter";
import blueShape from "./assets/blue-shape.svg";

const WorldMap = () => {
  // State
  const [world, setWorld] = useState([
    { name: "USA", color: "blue" },
    { name: "Canada", color: "red" }
  ]);
  const [country, setCountry] = useState({
    name: "name",
    color: "red"
  });

  const handleColor = event => {
    const { target } = event;
    setCountry(country => ({
      ...country,
      color: target.value
    }));
  };

  const handleName = event => {
    const { target } = event;
    setCountry(country => ({
      ...country,
      name: target.value
    }));
  };

  const handleSubmit = event => {
    event.preventDefault();

    console.log(country);

    setWorld([...world, country]);
    // setWorld(world => ({
    //   ...world
    // }));
    console.log(world);
  };

  // Effects
  useEffect(() => {
    Prism.highlightAll();
  }, []);

  useEffect(() => {
    console.log("world change");
  }, [world]);

  useEffect(() => {
    console.log("country change");
  }, [country]);

  return (
    <div>
      <h3>World Map</h3>
      <p>This app is all about mapping data using map()</p>
      <div className="app world-map">
        <div>
          <p>Country Name: {country.name}</p>
          <p>Flag Color: {country.color}</p>
        </div>

        <form>
          <h3>Create new Country:</h3>
          <input value={country.name} onChange={handleName}></input>
          <select onChange={handleColor}>
            <option>red</option>
            <option>green</option>
            <option>blue</option>
          </select>
          <button onClick={handleSubmit}>Create</button>
        </form>

        <div className="the-world-map">
          {world.map((country, i) => (
            <div className={`country ${i} ${country.color}`} key={i}>
              <p>{i}. </p>
              <p>{country.name}</p>
              <p>{country.color}</p>
            </div>
          ))}
          {/* <img src={blueShape} /> */}
        </div>
      </div>
      <A href="/work" className="back-link">
        Back to Work
      </A>
    </div>
  );
};

export default WorldMap;
