import React, { useState, useEffect } from "react";
import { useRoutes, usePath, A } from "hookrouter";
// import headerBG from "../../assets/header-bg-color.jpg";
// import headerBGSmall from "../../assets/header-bg-small.jpg";
// import AG from "../../assets/alex-grey-1.jpg";

const Header = props => {
  const path = usePath();
  const [drawer, setDrawer] = useState(false);
  const [pageHeader, setPageHeader] = useState("Home");
  const [theme, setTheme] = useState(false);
  let headerLinks = document.querySelectorAll("#header-nav nav a");
  let mobileLinks = document.querySelectorAll(
    "#mobile-header-nav .drawer nav a"
  );
  let themeSwitch = document.querySelectorAll(".theme-switch");

  // FUNCTIONS
  const setActiveLinks = () => {
    // Set Active Link class
    for (let i = 0; i < headerLinks.length; i++) {
      headerLinks[i].classList = "";
      let linkPath = headerLinks[i].getAttribute("href");
      if (path == linkPath) {
        headerLinks[i].classList.add("active-link");
      }
    }
    // if page width is < when drawer goes
    for (let i = 0; i < mobileLinks.length; i++) {
      mobileLinks[i].classList = "";
      let linkPath = mobileLinks[i].getAttribute("href");
      if (path == linkPath) {
        mobileLinks[i].classList.add("active-link");
      }
    }
  };

  function pageHeaderSetter() {
    if (path == "/") {
      setPageHeader("Home");
    } else {
      let cleanHeader = path.substring(1, path.length);
      setPageHeader(cleanHeader);
    }
  }

  const openDrawer = () => {
    setDrawer(!drawer);
  };

  const handleThemeSelect = () => {
    console.log("theme selected");
    setTheme(!theme);
    // console.log(AppContainer.classList.value);
    // AppContainer.classList.add("theme-light");
    //
  };

  // EFFECTS
  useEffect(() => {
    setActiveLinks();
    console.log("wtf");
  }, []);

  useEffect(() => {
    pageHeaderSetter();
    setActiveLinks();
    return () => {
      console.log(`Cleaning up ${path}`);
    };
  }, [path]);

  useEffect(() => {
    console.log("theem changed what");
    console.log(theme);
    let AppContainer = document.getElementById("App");

    if (theme) {
      AppContainer.classList.add("theme-light");
    } else {
      AppContainer.classList.remove("theme-light");
    }
  }, [theme]);

  useEffect(() => {
    let hamburger = document.getElementById("hamburger");
    if (drawer) {
      hamburger.classList.add("active");
    } else {
      hamburger.classList.remove("active");
    }
  }, [drawer]);

  return (
    <header>
      <div className="banner-bg"></div>
      <div id="header-nav">
        <nav>
          <A href="/" className="name-link">
            Stefan_Bobrowski
          </A>
          <A href="/work">Work</A>
          <A href="/learn">Learn</A>
          <A href="/personal">Personal</A>
          <A href="/contact">Contact</A>
        </nav>
      </div>

      <div id="mobile-header-nav">
        <div id="hamburger" className="not" onClick={openDrawer}>
          <span></span>
          <span></span>
          <span></span>
          <span></span>
        </div>
        {drawer ? (
          <div className="drawer">
            {/* <h3>Stefan_Bobrowski</h3> */}
            <nav>
              <A href="/">Home</A>
              <A href="/learn">Learn</A>
              <A href="/work">Work</A>
              <A href="/personal">Personal</A>
              <A href="/contact">Contact</A>
            </nav>
          </div>
        ) : (
          <div></div>
        )}
      </div>

      <div className="theme-switch-container">
        <span className={`${!theme ? "active" : ""}`}>Dark</span>
        <div className="selection-container" onClick={handleThemeSelect}>
          <div className={`theme-switch ${theme ? "on" : ""}`}></div>
        </div>
        <span className={`${theme ? "active" : ""}`}>Light</span>
      </div>

      <div className="page-header">
        <h1>{pageHeader}</h1>
        <div className="strike-through"></div>
      </div>
    </header>
  );
};

export default Header;
