  useEffect(() => {
    Prism.highlightAll();
    getChars();
  }, []);

  const getChars = async () => {
    // async function
    try {
      let response = await fetch("https://api.tvmaze.com/shows/82/cast");
      let chars = await response.json();
      setChars(chars);
    } catch (err) {
      console.log(err);
    }

