  // Header/Footer
  & > header,
  & > footer {
    width: 100%;
    .banner-bg {
      display: block;
      width: 100%;
      height: 24px;
      position: relative;
      background-image: url("./assets/banner-bg.jpg");
      background-repeat: no-repeat;
      background-position: center;
      background-size: cover;
      // transition: 0.4s linear;

      &:hover {
        animation: dimout 0.8s linear;
        animation-delay: 2s;

        a {
          display: inline-block;
        }
      }

      @keyframes dimout {
        0% {
          opacity: 1;
        }
        50% {
          opacity: 0.8;
        }
        100% {
          opacity: 1;
        }
      }

      &:hover:before {
        opacity: 0.75;
      }

      &:before {
        content: "";
        display: block;
        width: 100%;
        height: 24px;
        position: absolute;
        top: 0;
        right: 0;
        bottom: 0;
        left: 0;
        background-image: url("./assets/banner-bg-hover.jpg");
        background-position: inherit;
        background-repeat: inherit;
        background-size: inherit;
        opacity: 0;
        transition: 0.4s linear;
      }

      a {
        display: none;
        width: 164px;
        padding: 4px 0px;
        position: absolute;
        top: 100%;
        left: 1vw;
        font-size: 10px;
        text-align: center;
        z-index: 50;

        &:after {
          content: none;
        }
      }
    }

    nav a {
      display: block;
      line-height: 1;
      margin-right: 2vw;

      &:after {
        content: none;
      }
    }
  }

  // Header
  & > header {
    #header-nav {
      position: relative;
      max-width: $app-width;
      margin: 23px auto 0px auto;
      padding: 0px 24px;

      nav {
        display: flex;
        justify-content: flex-start;
        align-items: baseline;
        width: 100%;

        a {
          font-size: 17px;

          @media screen and (max-width: 750px) {
            font-size: 15px;
          }

          &.active-link {
            color: $gold;
          }

          &.name-link {
            @media screen and (max-width: 700px) {
              margin-bottom: 10px;
            }
          }

          &:hover {
            color: $gold;
          }

          &:first-of-type {
            font-family: $code-font;
            letter-spacing: 1;
            font-size: 25px;
            text-transform: none;

            @media screen and (max-width: 360px) {
              font-size: 25px;
            }
          }
        }

        @media screen and (max-width: 700px) {
          justify-content: center;
          a:not(.name-link) {
            display: none;
          }
        }
      }
    }

    #mobile-header-nav {
      display: none;
      margin-top: 4px;
      padding: 0px 13px;

      @media screen and (max-width: 700px) {
        display: block;
      }

      #hamburger {
        width: 30px;
        height: 23px;
        position: relative;
        margin: 10px auto 10px auto;
        transform: rotate(0deg);
        cursor: pointer;
        transition: 0.5s ease-in-out;

        &:hover span {
          background-color: $gold;
        }

        span {
          display: block;
          position: absolute;
          height: 3px;
          width: 100%;
          background-color: $yellow;
          border-radius: 9px;
          opacity: 1;
          left: 0;
          transform: rotate(0deg);
          transition: 0.25s ease-in-out;
        }

        span:nth-child(1) {
          top: 0px;
        }

        span:nth-child(2) {
          top: 9px;
          height: 2px;
        }
        span:nth-child(3) {
          top: 8px;
          height: 2px;
        }

        span:nth-child(4) {
          top: 16px;
        }

        &.active span:nth-child(1) {
          top: 9px;
          width: 0%;
          left: 50%;
        }

        &.active span:nth-child(2) {
          transform: rotate(45deg);
        }

        &.active span:nth-child(3) {
          transform: rotate(-45deg);
        }

        &.active span:nth-child(4) {
          top: 9px;
          width: 0%;
          left: 50%;
        }
      }

      .drawer {
        border: 2px solid $yellow;
        padding: 13px;

        nav {
          display: flex;
          flex-direction: column;
          align-items: center;

          a {
            margin: 5px;
          }
        }

        h3 {
          text-align: center;
        }
      }
    }

    .window-stats {
      display: flex;
      flex-direction: column;
      align-items: flex-end;
      position: absolute;
      top: 26px;
      right: 5px;
      font-family: $code-font;
      font-size: 9px;
      color: #fff;
    }

    .theme-switch-container {
      position: fixed;
      top: 77px;
      right: 0px;
      display: flex;
      align-items: center;
      padding: 6px 0px;
      background-color: $light-blue;
      z-index: 200;

      span {
        margin: 0px 7px;
        font-size: 10px;

        @media screen and (max-width: 800px) {
          &:first-of-type {
            font-size: 0px;
            &:after {
              font-size: 10px;
              content: "D";
            }
          }

          &:last-of-type {
            font-size: 0px;
            &:after {
              font-size: 10px;
              content: "L";
            }
          }
        }

