// HOME
.home-page {
  .home-intro {
    display: flex;
    position: relative;

    @media screen and (max-width: 700px) {
      flex-direction: column;
    }
  }

  .left-col-container {
    position: relative;
    text-align: center;
    transition: 0.4s ease;
    margin-right: 60px;
    flex: 0 0 200px;
  }

  .home-left-col {
    @media screen and (max-width: 700px) {
    }

    .stefan-pic {
      position: relative;
      display: block;
      width: 190px;
      height: 190px;
      margin: 0;
      border-radius: 50%;
      border: 5px solid $source;
      border-style: inset;
      background-image: url("../src/assets/pages/home/StefanBobrowski.png");
      background-size: 100%;
      background-position: center;
      animation: border-glow 9s linear infinite;
      animation-direction: alternate;
      z-index: 3;
      will-change: transform;

      @media screen and (max-width: 700px) {
        margin: 0 auto;
      }

      @keyframes border-glow {
        0% {
          border-color: $source;
          box-shadow-color: $source;
        }

        10% {
          border-color: $pink;
          box-shadow-color: $pink;
        }

        10% {
          border-color: $purple;
          box-shadow-color: $purple;
        }

        20% {
          border-color: $light-blue;
          box-shadow-color: $blue;
        }

        // 30% {
        //   border-color: $blue-green;
        //   box-shadow-color: $blue-green;
        // }

        40% {
          border-color: $green;
          box-shadow-color: $green;
        }

        50% {
          border-color: $yellow-green;
          box-shadow-color: $yellow-green;
        }

        60% {
          border-color: $yellow;
          box-shadow-color: $yellow;
        }

        70% {
          border-color: $gold;
          box-shadow-color: $gold;
        }

        80% {
          border-color: $orange;
          box-shadow-color: $orange;
        }

        100% {
          border-color: $bright-red;
          box-shadow-color: $bright-red;
        }
      }
      // Hover Effects
      &.gloss-effect {
        overflow: hidden;
        &:after {
          content: "";
          position: absolute;
          top: 0;
          left: 0;
          width: 0;
          height: 100%;
          background-color: rgba(255, 255, 255, 0.44);
        }
        &:hover {
          &:after {
            width: 110%;
            background-color: rgba(255, 255, 255, 0);
            transition: 0.5s ease;
          }
        }
      }

      &.ssj-effect {
        transition: all 0.2s linear;
        &:hover {
          animation: ssj 0.5s ease infinite;
          filter: brightness(1.1) drop-shadow(0px 0px 15px $gold);

          .ssj {
            display: block;
          }
        }

        .ssj {
          display: none;
          position: absolute;
          bottom: 90px;
          left: 10px;
          transform: rotate(7deg) skew(-1deg, 0deg);
          opacity: 0.9;
          width: 200px;

          img {
            display: block;
            width: 100%;
          }
        }
      }

      .ssj {
        display: none;
      }

      @keyframes ssj {
        0% {
          box-shadow: 0 0 3px 3px $white, 0 0 6px 6px $gold,
            0 0 12px 12px $yellow;
        }
        40% {
          box-shadow: 0 0 10px 10px $white, 0 0 20px 20px $gold,
            0 0 40px 40px $yellow;
        }

        90% {
          box-shadow: 0 0 3px 3px $white, 0 0 6px 6px $gold,
            0 0 12px 12px $yellow;
        }

        100% {
          box-shadow: 0 0 3px 3px $white, 0 0 6px 6px $gold,
            0 0 12px 12px $yellow;
        }
      }

      &.ripple-effect {
        &:hover {
          &:before,
          &:after,
          .ripple {
            opacity: 1;
          }
        }

        &:before,
        &:after,
        .ripple {
          content: "";
          display: block;
          width: 100%;
          height: 100%;
          border: 3px solid #fff;
          opacity: 0;
          position: absolute;
          left: 0;
          top: 0;
          // transition: 0.4s ease-out;
        }

        &:before {
          border-radius: 36% 44% 49% 55% / 45% 53% 39% 52%;
          // border-style: inset dashed groove inset;
          animation: 0.9s ripple linear infinite;
          border-color: $gold;
        }

        &:after {
          border-radius: 36% 44% 49% 55% / 45% 53% 39% 52%;
          // border-style: inset inset dashed groove;
          animation: 1.1s ripple linear infinite;
          border-color: $light-blue;
        }

        .ripple {
          border-radius: 36% 44% 49% 55% / 45% 53% 39% 52%;
          // border-style: inset dashed groove inset;
          animation: 1.5s ripple linear infinite;

          &:first-of-type {
            animation: 1.3s ripple linear infinite;
          }

          &:last-of-type {
            animation: 1.7s ripple linear infinite;
          }
        }

        @keyframes ripple {
          0% {
            -webkit-transform: rotate(0deg) scale(1);
            transform: rotate(0deg) scale(1);
            border-color: transparent;
          }
          25% {
            border-color: $bright-red;
          }
          33% {
            border-color: $orange;
          }
          50% {
            border-color: $gold;
          }
          66% {
            border-color: $lime-green;
          }

          75% {
            border-color: $light-blue;
          }
          100% {
            transform: rotate(180deg) scale(2);
            // border-color: $source;
            border-color: $light-purple;
            opacity: 0;
          }
        }
      }

      &.coin-effect {
        position: relative;
        z-index: 3;
        transform: none;
        transition: 1.5s ease;

        &:hover {
          transform: skew(180deg, 180deg);
        }
      }

      &.morph-effect {
        background-color: silver;
        &:hover {
          animation: 3s morph linear forwards;
        }

        @keyframes morph {
          0% {
            border-radius: 50%;
            transform: rotate(0deg);
          }
          25% {
            border-radius: 3px;
          }

          50% {
            width: 120px;
            height: 120px;
          }

          75% {
            width: 60px;
            height: 60px;
            border-radius: 3px;
            transform: rotate(-30deg);
          }

          100% {
            border-radius: 50%;
            width: 190px;
            height: 190px;
            transform: rotate(360deg);
          }
        }
      }
    }

    .titles {
      display: flex;
      flex-direction: column;
      align-items: center;
      margin-top: 10px;

      .stefan-name {
        margin-bottom: 5px;
