      transform: perspective(327px) rotateX(28deg);
      z-index: 10;

      .table-text {
        width: 320px;
        position: absolute;
        left: 0;
        right: 0;
        top: 108px;
        margin: auto;
        text-align: center;

        h4,
        h5 {
          font-family: "Rye", cursive;
          margin: 0;
          color: rgb(1, 95, 95);
          text-shadow: 0px 0px 0px 0px black;
        }
        h4 {
          display: inline-block;
          margin-bottom: 4px;
          font-size: 24px;
          text-transform: uppercase;
        }

        h5 {
          font-size: 14px;
          font-style: italic;
          font-family: "Rye", cursive;
        }
      }

      #deck {
        .card {
          position: absolute;
          top: 150px;
          right: 120px;
          border: 5px solid $source;
          box-shadow: -4px 4px 1px 0px rgb(143, 143, 143);
        }
      }

      .dealer-hand,
      .player-hand {
        display: flex;
        justify-content: center;
        width: 100%;
        max-width: 400px;
        margin: 0 auto;
        position: relative;

        .hand-value {
          position: absolute;
          right: 80px;
          padding: 0px 4px;
          border: 1px solid black;
          background-color: seagreen;
          font-size: 18px;
          color: #fff;
        }
      }

      .dealer-hand {
        top: -20px;
        .hand-value {
          top: 110%;
        }
      }
      .player-hand {
        bottom: -20px;
        .hand-value {
          bottom: 110%;
        }
      }

      .bet-circle {
        display: block;
        width: 60px;
        height: 60px;
        position: absolute;
        bottom: 30px;
        left: 170px;
        border: 4px solid gray;
        border-radius: 50%;
        span {
          width: 82px;
          position: absolute;
          font-size: 14px;
          top: -18px;
          left: -60px;
        }
      }

      .bet,
      .dd {
        display: block;
        position: absolute;
        top: 210px;
        left: 20px;
        font-size: 30px;
        text-align: center;

        &.place-bet {
          animation: place-it 1s linear forwards;
        }

        @keyframes place-it {
          0% {
          }

          100% {
            top: 5px;
            left: 5px;
          }
        }
        &.place-dd {
          animation: place-dd 1s linear forwards;
        }

        @keyframes place-dd {
          0% {
          }

          100% {
            top: 13px;
            left: 13px;
          }
        }
      }
    }

    .message-log {
      border: 3px solid $black;
      border-radius: 5px;
      position: absolute;
      top: 6px;
      right: 8px;
      width: 270px;
      background-color: $source;
      z-index: 50;
      #messages {
        display: block;
        height: 126px;
        border: 2px solid maroon;
        border-radius: 3px;
        margin: 5px;
        padding: 10px;
        overflow-y: scroll;

        p {
          font-size: 14px;
          margin: 0;

          &:nth-of-type(odd) {
            background-color: rgb(223, 223, 223);
          }
        }
      }

      &:before {
        content: "";
        display: block;
        width: 0;
        height: 0;
        border-left: 50px solid transparent;
        border-bottom: 50px solid $black;
        position: absolute;
        right: 101%;
        bottom: 30px;
      }
    }

    .results {
      position: absolute;
      width: 350px;
      left: 0;
      right: 0;
      bottom: 150%;
      display: flex;
      flex-direction: column;
      justify-content: center;
      align-items: center;
      margin: auto;
      padding: 10px;
      border: 3px solid maroon;
      background-color: rgba(black, 0.7);
      z-index: 100;
      text-align: center;
      p {
        font-size: 26px;
        margin-bottom: 10px;
        color: $white;
      }
    }

    footer {
      display: flex;
      align-items: center;
      justify-content: space-around;
      position: absolute;
      bottom: 20px;

      .bank {
        margin-left: 20px;
        border: 2px solid aquamarine;
        padding: 5px;
        color: #fff;
        background-color: darken(aquamarine, 50%);
        p {
          font-size: 30px;
          color: #fff;
        }
      }
      #player-controls {
        display: flex;
        margin: 0px 20px 0px 20px;

        button {
          margin: 0px 2px;

          &:first-of-type {
            margin-right: 20px;
          }

          &:nth-of-type(3) {
            margin-right: 20px;
          }

          &.bet-control {
            border-radius: 25%;
            margin-right: 30px;
          }
        }
      }
    }

    // Blackjack third time's a charm
    .debug-console {
      display: none;
      position: absolute;
      bottom: 0px;
      right: 7px;
      border: 2px solid $bright-red;
      padding: 7px;
    }
  }

