  }
  // WorldMap

  &.world-map {
    form {
      border: 1px solid black;
    }

    .the-world-map {
      display: flex;
    }
    .country {
      display: flex;
      flex-direction: column;
      align-items: center;
      width: 100px;
      height: 100px;
      outline: 1px solid black;

      &.red {
        background-color: red;
      }

      &.green {
        background-color: green;
      }

      &.blue {
        background-color: blue;

        p {
          color: white;
        }
      }

      p {
        color: white;
      }
    }
  }

  &.image-search {
    p {
      color: black;
    }
    .images-container {
      display: flex;
      flex-wrap: wrap;
      .image-container {
        flex: 0 1 33%;

        img {
          display: block;
          width: 100%;
        }
      }
    }
  }

  &.data-visuals {
    .graph-container {
      display: block;
      width: 300px;
      height: 300px;
      border: 1px solid red;
      overflow: hidden;
      position: relative;

      .graph {
        display: flex;
        align-items: flex-end;
        width: 300px;
        height: 300px;
        position: absolute;
        top: 0;
        left: 0;

        > div {
          display: block;
          width: 30px;
          margin-left: 10px;
          border: 1px solid black;
          opacity: 0.8;
        }
      }

      .color-key {
        display: flex;
        align-items: center;
        .color-box {
          display: block;
          width: 30px;
          heigth: 30px;
        }
      }
    }
  }

  // City Life
  &.city-life {
    padding: 8px;
    background-color: silver;

    header {
      display: flex;
      justify-content: center;
      margin-bottom: 8px;

      div {
        margin-right: 20px;
        padding: 6px 10px;
        border: 1px solid black;
        border-radius: 4px;
        background-color: lightblue;

        &.menu {
        }
      }
    }

    #game-board {
      display: flex;
      justify-content: center;
      align-items: center;
      width: 380px;
      height: 380px;
      margin: 0 auto;
      background-color: gray;

      .city-column {
        display: flex;
        flex-direction: column;

        .city-block {
          position: relative;
          display: flex;
          justify-content: center;
          align-items: center;
          width: 58px;
          height: 58px;
          margin: 7px;
          border-radius: 3px;
          box-shadow: 0px 2px 0px 3px black;
          font-size: 22px;
          background-color: sandybrown;
          // user-select: none; /* Standard */

          &.dirt {
            background-color: SADDLEBROWN;
          }

          &.grass {
            background-color: DARKGREEN;
          }

          &.nice-grass {
            background-color: GREEN;
          }

          &.pavement {
            background-color: DARKGRAY;
          }

          &.brick {
            background-color: BROWN;
          }

          &:hover {
            cursor: pointer;
          }

          &.connected {
            &:after {
              content: "";
              display: block;
              height: calc(100% + 6px);
              width: 10px;
              position: absolute;
              background-color: inherit;
            }
          }

          &.con-up {
          }

          &.con-down {
          }

          &.con-right {
            &:after {
              top: -1px;
              left: 98%;
              border-top: 1px solid black;
              border-bottom: 5px solid black;
              z-index: 3;
            }
          }

          &.con-left {
            &:after {
              top: -1px;
              right: 98%;
              border-top: 1px solid black;
              border-bottom: 5px solid black;
            }
          }
        }
      }
    }
  }
}

.back-link {
  margin-top: 18px;
  float: right;
  text-transform: uppercase;
  font-size: 28px;
}

// Personal

.personal-page {
  section {
    // border: 1px solid $gold;
  }

  .hobbies-container {
  }
  .hobbies {
    .hobby {
      img {
        display: block;
        width: auto;
        max-width: 100%;
      }
    }
  }

  // Hobby specific styles
  .hardware-list {
    max-width: 100%;
    padding-left: 10px;
    list-style-type: none;
    li {
      font-size: 14px;
      color: #fff;
      span {
        text-align: right;

        display: inline-block;
        width: 150px;
        margin-right: 5px;
        font-size: 18px;
        color: $gold;
      }
    }
  }

  .food-icons {
    display: flex;
    align-items: center;
    font-size: 38px;
    color: #fff;

    p {
      font-size: 26px;
    }
    span {
    }
  }

  .reading-list {
    li {
      margin-left: 10px;
      ul {
        padding-left: 0px;
      }
    }
  }
}

.art-thumb {
  img {
    max-width: 100%;
  }
}

// CATS

.cat-stats {
  display: flex;

  > div {
    flex: 1 1 50%;
    display: flex;
    flex-direction: column;
    border: 1px solid $gold;
    margin: 10px;
    padding: 10px;

    p {
      max-width: 100%;
    }
  }
}

.cat-slider-container {
  display: flex;
  justify-content: center;
  align-items: center;

  > span {
    font-size: 28px;

    &:hover {
      cursor: pointer;
    }
  }
  .cat-slider {
    .cat-slide {
      display: none;
      position: relative;
      border: 1px solid $gold;
      img {
        display: block;
        width: auto;
        margin: 0 auto;
      }
      p {
        margin: 0 auto;
        text-align: center;
        background-color: rgba(0, 0, 0, 80);
      }

      &.current-slide {
        display: block;
      }
    }
  }
}

.cat-gallery-container {
  display: flex;
  flex-wrap: wrap;
  margin-bottom: 20px;

  .cat-item {
    position: relative;
    width: 200px;
    height: 200px;
    border: 1px solid $light-blue;
    overflow: hidden;

    &:hover {
      cursor: pointer;
      p {
        opacity: 1;
      }

      img {
        filter: brightness(100%);
        transition: 0.4s ease;
      }
    }
    img {
      display: block;
      width: 100%;
      image-orientation: from-image;
      filter: brightness(50%);
    }

    p {
      width: 100%;
      padding: 2px 0px;
      text-align: center;
      font-size: 12px;
      opacity: 0;
      position: absolute;
      bottom: 0;
      background-color: rgba(0, 0, 0, 0.8);
      transition: 0.4s ease;
    }
  }
}

.cat-modal {
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  position: fixed;
  top: 0;
  right: 0;
  bottom: 0;
  left: 0;
  height: 100vh;
  width: 100vw;
  overflow: hidden;
  background-color: rgba(0, 0, 0, 0.9);
  z-index: 300;

  .window {
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    border: 1px solid $gold;
    height: 720px;
    width: 100%;
    max-width: 720px;
    padding: 20px;
  }

  .close-button {
    position: relative;
    top: -10px;
    right: -120px;
    color: white;

    &:hover {
      cursor: pointer;
    }
  }

  .modal-img {
    img {
      display: block;
      max-width: 433px;
    }
  }

  .modal-caption {
    padding: 10px;
    p {
      font-size: 16px;
    }
  }
}

// Contact Page
.contact-page {
  .business-card {
    display: flex;
    padding: 12px;
    border: 1px solid $gold;
    border-radius: 8px;

    .photo {
      width: 180px;
      border: 1px solid $gold;
      border-radius: 8px;
      overflow: hidden;
    }
  }
  .contact-links {
    margin: 20px auto;
    padding: 20px;
    border: 1px solid $border-color;
    background-color: rgba(teal, 0.5);
  }

  .contact-icons {
    display: flex;
    justify-content: space-around;
    align-items: center;
    margin: 22px auto;

    @media screen and (max-width: 540px) {
      flex-direction: column;
    }

    .email-container {
      position: relative;
      width: 270px;
      height: 170px;
      border: 3px solid white;
      border-radius: 4px;
      background-color: #ddd;

      @media screen and (max-width: 540px) {
        margin-bottom: 20px;
      }

      h3 {
        margin-bottom: 63px;
        font-size: 20px;
        font-weight: bold;
        color: black;

        span {
          margin-right: 4px;
        }
      }

      a {
        font-size: 18px;
        color: black;
        z-index: 2;

        &:hover {
          color: $bright-red;
        }
      }

      > div {
        position: relative;
        top: 22px;
        display: flex;
        flex-direction: column;
        align-items: center;
        justify-content: flex-end;
        z-index: 7;
      }

      &:after {
        content: "";
        position: absolute;
        top: 0;
        left: 0;
        display: block;
        border: 133px solid transparent;
        border-top: 90px solid #eee;
        border-radius: 5px;
      }
    }

    .phone-container {
      position: relative;
      width: 160px;
      height: 280px;
      border: 6px solid #111;
      border-radius: 7px;
      background-color: #222;

      .phone-screen {
        width: 96%;
        height: 87%;
        top: 3px;
        margin: auto;
        padding: 0 5px;
        position: relative;
        display: flex;
        flex-direction: column;
        align-items: center;
        justify-content: center;
        text-align: center;
        background-color: rgb(1, 148, 148);

        &.off {
          background-color: black;
          > * {
            display: none;
          }
        }

        h3 {
          font-size: 20px;
          color: #fff;
        }

        .phone-number {
          margin-bottom: 13px;
          filter: blur(1px);
          color: $gold;
          transition: 0.3s linear;

          &:hover {
            filter: blur(0);
          }
        }
      }

      .phone-bottom {
        display: block;
        width: 100%;
        height: 10%;
        position: absolute;
        bottom: 0;
        left: 0;
        background-color: #333;

        .home-button {
          display: block;
          width: 22px;
          height: 22px;
          position: absolute;
          bottom: 3px;
          left: 0;
          right: 0;
          margin: auto;
          border: 1px inset #666;
          border-radius: 50%;
          background-color: #333;

          &:hover {
            cursor: pointer;
            background-color: #444;
          }
        }
      }
    }
  }

  .skype-container {
    display: flex;
    justify-content: center;
    align-items: center;
    margin: 0 auto;
    text-align: center;

    img {
      filter: brightness(1);
      transition: 0.3s ease;
    }
    span {
      margin-left: 10px;
      font-size: 30px;
      color: #ccc;
      transition: 0.3s ease;
    }

    &:hover {
      img {
        filter: brightness(1.2);
      }
      span {
        color: #fff;
      }
    }
  }
}

