import React, { useState, useEffect } from "react";
import { useRoutes, usePath, A } from "hookrouter";

const BlackJack = () => {

  const defaultGameStates = {
    bet: 0,
    dealt: 0,
    playerTurn: 0,
    pAces: 0,
    hit: 0,
    stand: 0,
    dd: 0,
