import React, { useState, useEffect } from "react";
import { useRoutes, usePath, A } from "hookrouter";

const ToDo = () => {
  const [todo, setTodo] = useState("");
  const [todoList, setTodoList] = useState([
    "Laundry",
    "Grocery Shopping",
    "Have a dream"
  ]);
  const [message, setMessage] = useState("");

  useEffect(() => {
    Prism.highlightAll();
  }, []);

  // useEffect(() => {
  //   console.log("todo change");
  // }, [todo]);

  // useEffect(() => {
  //   console.log("todolist change");
  // }, [todoList]);

  useEffect(() => {
    console.log("msg change");
    // handleMessage();
  }, [message]);

  const handleInput = e => {
    const { target } = e;
    setTodo(target.value);
  };

  const handleMessage = msg => {
    setMessage(msg);
    const colorMsg = document.getElementById("output-message");
    colorMsg.classList.toggle("change-color");
  };

  const handleSubmit = e => {
    e.preventDefault();
    const { target } = e;

    if (todo == "") {
      handleMessage("Empty To-Do!");
    } else {
      setTodoList([...todoList, todo]);
      setTodo("");
      target.parentNode.childNodes[1].value = ""; // reset form
      handleMessage("To-Do Created!");
    }
  };

  const handleDelete = e => {
    e.preventDefault();
    const { target } = e;
    let todoId = target.parentNode.getAttribute("key");
    let todoListCopy = [...todoList];
    todoListCopy.splice(todoId, 1);
    setTodoList(todoListCopy);
    handleMessage("To-Do Deleted!");
  };

  return (
    <div>
      <h3>ToDo App</h3>
      <p>
        My implementation of the classic <i>To-Do List</i> app.
      </p>
      <div className="app to-do">
        <form>
          <h3>Create a new ToDo!</h3>
          <input
            onChange={handleInput}
            placeholder="What to do...?"
            maxLength="64"
          />
          <button onClick={handleSubmit}>Create</button>
          <div id="to-do-message">
            <p>Output:</p>
            <span id="output-message">{message}</span>
          </div>
        </form>
        <div className="to-do-board-container">
          <h3>ToDo List:</h3>
          <div className="to-do-board">
            {todoList.map((atodo, i) => (
              <div className="todo" key={i}>
                <p>{atodo}</p>
                <span onClick={handleDelete}>❌</span>
              </div>
            ))}
          </div>
        </div>
      </div>

      <h3>💻 Code:</h3>
      <div className="file-name">
        <span>
          <i className="code-icon js-icon">{"JS"} </i>ToDo.js
        </span>
      </div>
      <pre className="line-numbers">
        <code className="language-jsx">
          {`import React, { useState, useEffect } from "react";
import "../styles.scss";

const ToDo = () => {
  const [todo, setTodo] = useState("");
  const [todoList, setTodoList] = useState([
    "Laundry",
    "Grocery Shopping",
    "Have a dream"
  ]);
  const [message, setMessage] = useState("");

  return (
    <div className="app to-do">
      <form>
        <h3>Create a new ToDo!</h3>
        <input onChange={handleInput} placeholder="What to do...?" />
        <button onClick={handleSubmit}>Create</button>
        <p id="to-do-message">{message}</p>
      </form>
      <div className="to-do-board-container">
        <h3>ToDo List:</h3>
        <div className="to-do-board">
          {todoList.map((atodo, i) => (
            <div className="todo" key={i}>
              <p>{atodo}</p>
              <span onClick={handleDelete}>❌</span>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default ToDo;
`}
        </code>
      </pre>

      <div className="file-name">
        <span>
          <i className="code-icon css-icon">{"#"}</i> styles.css
        </span>
      </div>
      <pre className="line-numbers">
        <code className="language-css">
          {`@import url("https://fonts.googleapis.com/css?family=Indie+Flower&display=swap");
          
  .to-do {
    display: flex;
    font-family: "Indie Flower";

    h3 {
      font-size: 26px;
    }

    form {
      flex: 0 1 auto;
      padding: 13px;
      border: 1px solid black;
      border-right: none;
      text-align: center;

      input {
        padding: 8px;
        border: 1px solid black;
        border-radius: 5px;
        font-size: 18px;
      }

      button {
        display: block;
        margin: 10px auto 0px auto;
        border-radius: 5px;
      }

      #to-do-message {
        opacity: 0;
        display: inline-block;
        margin-top: 40px;
        padding: 10px;
        font-size: 20px;
        color: #f275ad;
        border: 2px solid #f275ad;

        &.show-message {
          animation: fade-out 2s ease-in;
        }

        @keyframes fade-out {
          0% {
            opacity: 1;
          }
          100% {
            opacity: 0;
          }
        }
      }
    }

    .to-do-board-container {
      padding: 13px;
      flex: 1 1 auto;
      border: 1px solid black;

      .to-do-board {
        display: flex;
        flex-wrap: wrap;

        .todo {
          flex: 0 1 30%;
          display: flex;
          justify-content: center;
          align-items: center;
          position: relative;
          // width: 154px;
          min-height: 154px;
          // width: 31%;
          min-width: 154px;
          // max-width: 180px;
          // min-height: 140px;
          margin: 1%;
          padding: 2px 4px;
          border: 1px solid black;
          text-align: center;

          &:nth-of-type(4n) {
            background-color: #ffff97;
          }

          &:nth-of-type(4n-1) {
            background-color: #fbae4a;
          }

          &:nth-of-type(4n + 2) {
            background-color: #f275ad;
          }

          &:nth-of-type(4n + 1) {
            background-color: #79cbc5;
          }

          p {
            margin-bottom: 0px;
            font-size: 20px;
            word-wrap: break-word;
          }

          span {
            position: absolute;
            top: 2px;
            right: 4px;

            &:hover {
              cursor: pointer;
            }
          }
        }
      }
    }
  }
`}
        </code>
      </pre>
      <A href="/work" className="back-link">
        Back to Work
      </A>
    </div>
  );
};

export default ToDo;
